
public class ComputeEx01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a,b;
		int result;
		
		a=100;
		b=25;
		
		result=a+b;
		//sout 타이핑 후 ctrl+space 단축키 사용
		System.out.println(a+"+"+b+"="+result);
		
		result=a-b;
		System.out.println(a+"-"+b+"="+result);
		
		result=a*b;
		System.out.println(a+"*"+b+"="+result);
		
		result=a/b;
		System.out.println(a+"/"+b+"="+result);
	}

}
